import { Icons, IconName as RaycastIconName, StarsIcon } from "@raycast/icons";
import { SVGProps } from "react";

export type IconName =
  | RaycastIconName
  | "react"
  | "swift"
  | "python"
  | "nextjs";

export function IconComponent(
  props: { icon: IconName } & SVGProps<SVGSVGElement>
) {
  if (props.icon === "react") {
    return <ReactIcon {...props} />;
  }
  if (props.icon === "swift") {
    return <SwiftIcon {...props} />;
  }
  if (props.icon === "python") {
    return <PythonIcon {...props} />;
  }
  if (props.icon === "nextjs") {
    return <NextJsIcon {...props} />;
  }
  if (Icons[props.icon]) {
    const Icon = Icons[props.icon];
    return <Icon {...props} />;
  }
  return <StarsIcon {...props} />;
}

